import React from "react";
// import CommitTable from "../../Table/CommitTable";
import { Card, CardContent, Box, Typography } from "@mui/material";
import StarIcon from "@mui/icons-material/Star";
import ForkRightIcon from "@mui/icons-material/ForkRight";

import { useEffect, useState } from "react";
import { getCommits } from "../../controller/controller";
import axios from "axios";
import {
  Table,
  TableBody,
  TableContainer,
  TableHead,
  Paper,
} from "@mui/material";

export default function Front({ handleflip, name, repoDetails }) {
  return (
    <Card className={"card-container"} key={name} onClick={handleflip}>
      <CardContent>
        <Typography
          gutterBottom
          variant="h5"
          component="div"
          className="card-container__title"
        >
          {repoDetails.repoName}
        </Typography>
        <Typography variant="body2" className="card-container__des">
          {repoDetails.repoDescription}
        </Typography>
      </CardContent>
      <CardContent className="card-container__content">
        {getFrontDetailDisplays(repoDetails).map((detail, i) => {
          return (
            <Box key={detail.key + i} className="card-container__content--box">
              {detail.icon}
              {detail.detail}
            </Box>
          );
        })}
      </CardContent>
    </Card>
  );
}

const colorArray = [
  { name: "Java", color: "#b07219" },
  { name: "Go", color: "#00ADD8" },
  { name: "Python", color: "#3572A5" },
  { name: "Kotlin", color: "#A97BFF" },
  { name: "HTML", color: "#e34c26" },
  { name: "C", color: "#555555" },
  { name: "Scala", color: "#c22d40" },
  { name: "JavaScript", color: "#f1e05a" },
  { name: "TypeScript", color: "#3178c6" },
  { name: "C++", color: "#f34b7d" },
  { name: "Jupyter Notebook", color: "#DA5B0B" },
  { name: "Groovy", color: "#4298b8" },
];

const getFrontDetailDisplays = (repoDetails) => {
  return [
    {
      icon: (
        <span
          className="dot"
          style={{
            height: "15px",
            width: "15px",
            backgroundColor: repoDetails.langColor,
            borderRadius: "50%",
            margin: "auto .5rem auto auto",
          }}
        />
      ),
      detail: <Typography size="small"> {repoDetails.repoLanguage}</Typography>,
      key: repoDetails.full_name,
    },
    {
      icon: (
        <StarIcon
          className="star"
          style={{
            stroke: "#ffe135",
            color: "#ffe135",
            margin: "auto .2rem auto auto",
          }}
        />
      ),
      detail: <Typography size="small"> {repoDetails.starCount}</Typography>,
      key: repoDetails.full_name,
    },
    {
      icon: (
        <ForkRightIcon
          className="star"
          style={{
            margin: "auto .2rem auto auto",
          }}
        />
      ),
      detail: <Typography size="small"> {repoDetails.forkCount}</Typography>,
      key: repoDetails.full_name,
    },
    {
      icon: (
        <span
          // className="label"
          style={{
            // backgroundColor: langColor,
            fontWeight: "bold",
            margin: "auto .5rem auto auto",
          }}
        >
          Date Created:
        </span>
      ),
      detail: (
        <Typography size="small">
          {" "}
          {new Date(repoDetails.dateCreated).toLocaleString()}
        </Typography>
      ),
      key: repoDetails.full_name,
    },
  ];
};
