import React from "react";
// import CommitTable from "../../Table/CommitTable";
import { Card, CardContent, Box, Typography } from "@mui/material";
import { FrontDetails } from "../../../utility/DataGenerator";
import { useEffect, useState } from "react";
import { getCommits } from "../../controller/controller";
import axios from "axios";
import {
  Table,
  TableBody,
  TableContainer,
  TableHead,
  Paper,
} from "@mui/material";

function Back({ handleflip, name, fullName }) {
  const [commitData, setCommitData] = useState([]);

  const getCommitsForRepo = async (name) => {
    try {
      const url = `https://api.github.com/repos/${name}/commits`;
      const response = await axios.get(url);
      if (response.status === 200) {
        const formattedCommitData = response.data.map((data) => {
          console.log("data", data);
          // ({
          //   Title: data.commit.message,
          //   Username: author?.login || commit.author.name,
          //   Hash: sha,
          //   DateCreated: new Date(commit.author.date).toLocaleString(),
          // })
        });
        setCommitData(response.data);
      } else {
        return [];
      }
    } catch (error) {}
  };

  useEffect(() => {
    (async () => {
      await getCommitsForRepo(fullName);
    })();
    return () => {};
  }, [isFlipped]);

  return (
    <Card
      key={name}
      className="card-container"
      sx={{ overflow: "auto", height: "auto" }}
      onClick={handleflip}
    >
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {name} - Commit Log
        </Typography>
        <TableContainer component={Paper}>
          <Table aria-label="simple table" className="commit-table">
            {/* <TableHead>{headerGenerator(headers)}</TableHead>
            <TableBody>{rowGenerator(data)}</TableBody> */}
          </Table>
        </TableContainer>{" "}
      </CardContent>
    </Card>
  );
}

function Front({ handleflip, name, repoDetails }) {
  return (
    <Card className={"card-container"} key={name} onClick={handleflip}>
      <CardContent>
        <Typography
          gutterBottom
          variant="h5"
          component="div"
          className="card-container__title"
        >
          {repoDetails.repoName}
        </Typography>
        <Typography variant="body2" className="card-container__des">
          {repoDetails.repoDescription}
        </Typography>
      </CardContent>
      <CardContent className="card-container__content">
        {FrontDetails(repoDetails).map((detail, i) => {
          return (
            <Box key={detail.key + i} className="card-container__content--box">
              {detail.icon}
              {detail.detail}
            </Box>
          );
        })}
      </CardContent>
    </Card>
  );
}

export default function CardDisplay({ repoDetails, isLoading }) {
  const [isFlipped, setisFlipped] = useState(false);

  const handleflip = async () => {
    setisFlipped(!isFlipped);
  };
  const handleflipBack = () => {
    setisFlipped(false);
  };

  return (
    <>
      {isFlipped ? (
        <Back
          name={repoDetails.repoName}
          handleflip={handleflipBack}
          fullName={repoDetails.full_name}
        />
      ) : (
        <Front
          name={repoDetails.repoName}
          repoDetails={repoDetails}
          handleflip={handleflip}
        />
      )}
    </>
  );
}
