import React from "react";
// import CommitTable from "../../Table/CommitTable";
import {
  Card,
  CardContent,
  Box,
  Typography,
  Table,
  TableBody,
  TableContainer,
  TableHead,
  Paper,
} from "@mui/material";
import StarIcon from "@mui/icons-material/Star";
import ForkRightIcon from "@mui/icons-material/ForkRight";

import { useEffect, useState } from "react";
import axios from "axios";

export default function Front({ handleflip, name, repoDetails }) {
  return (
    <Card className={"card-container"} key={name} onClick={handleflip}>
      <CardContent>
        <Typography
          gutterBottom
          variant="h5"
          component="div"
          className="card-container__title"
        >
          {repoDetails.repoName}
        </Typography>
        <Typography variant="body2" className="card-container__des">
          {repoDetails.repoDescription}
        </Typography>
      </CardContent>
      <CardContent className="card-container__content">
        {getFrontDetailDisplays(repoDetails).map((detail, i) => {
          return (
            <Box key={i} className="card-container__content--box">
              {detail.icon}
              {detail.detail}
            </Box>
          );
        })}
      </CardContent>
    </Card>
  );
}

const languageColorMap = {
  Java: "#b07219",
  Go: "#00ADD8",
  Python: "#3572A5",
  Kotlin: "#A97BFF",
  HTML: "#e34c26",
  C: "#555555",
  SShell: "#000000",
  Scala: "#c22d40",
  JavaScript: "#f1e05a",
  TypeScript: "#3178c6",
  "C++": "#f34b7d",
  "Jupyter Notebook": "#DA5B0B",
  Groovy: "#4298b8",
};

const getFrontDetailDisplays = (repoDetails) => {
  return [
    {
      icon: (
        <span
          className="dot"
          style={{
            height: "15px",
            width: "15px",
            backgroundColor: languageColorMap[repoDetails.language] || "#fff",
            borderRadius: "50%",
            margin: "auto .5rem auto auto",
          }}
        />
      ),
      detail: <Typography size="small"> {repoDetails.language}</Typography>,
    },
    {
      icon: (
        <StarIcon
          className="star"
          style={{
            stroke: "#ffe135",
            color: "#ffe135",
            margin: "auto .2rem auto auto",
          }}
        />
      ),
      detail: (
        <Typography size="small"> {repoDetails.stargazers_count}</Typography>
      ),
    },
    {
      icon: (
        <ForkRightIcon
          className="star"
          style={{
            margin: "auto .2rem auto auto",
          }}
        />
      ),
      detail: <Typography size="small"> {repoDetails.forks_count}</Typography>,
    },
    {
      icon: (
        <span
          style={{
            fontWeight: "bold",
            margin: "auto .5rem auto auto",
          }}
        >
          Date Created:
        </span>
      ),
      detail: (
        <Typography size="small">
          {new Date(repoDetails.created_at).toLocaleString()}
        </Typography>
      ),
    },
  ];
};
