import { useContext, useEffect, useState } from "react";
import Navbar from "./components/Navigation/Navbar";
import RepositoryInfomation from "./components/RepositoryInfo/RepositoryInformation";
import Alert from "@mui/material/Alert";
import Button from "@mui/material/Button";
import axios from "axios";
function App() {
  const [repos, setRepos] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  // DataGenerator(repositiory, colorArray);
  const getRepository = async (organizationName) => {
    setIsLoading(true);
    try {
      const response = await axios.get(
        `https://api.github.com/orgs/${organizationName}/repos`
      );
      setIsLoading(false);
      if (response.status === 200) {
        return response.data;
      } else {
        return [];
      }
    } catch (error) {
      setIsLoading(false);
      setIsError(true);
    }
  };

  useEffect(() => {
    getRepository("Netflix");
  }, []);
  useEffect(() => {
    if (!datastore.datastoreLoading) {
      if (datastore.repos.length > 0) {
        setRepos(datastore.repos);
        setIsLoading(false);
      }
    }
  }, [datastore]);

  const handleSearch = (searchInput) => {
    const repoInfo = getRepository(searchInput);
    Promise.all([repoInfo]).then((values) => {
      if (values[0].length > 0) {
        datastoreActions.setRepo(values[0]);
        setIsLoading(false);
      } else {
        setIsError(true);
        setIsLoading(true);
      }
    });
  };
  const resetDatabase = () => {
    handleSearch("Netflix");
    setIsError(false);
    setIsLoading(true);
  };

  const handleNoResults = () => {
    return (
      <Alert
        action={
          <Button onClick={resetDatabase} color="inherit" size="small">
            RELOAD
          </Button>
        }
        severity="error"
      >
        Repository not found, please press the button to the right and try
        another repository.
      </Alert>
    );
  };
  return (
    <>
      <Navbar search={handleSearch} isLoading={isLoading} />
      {error && handleNoResults()}
      <RepositoryInfomation repositories={repos} isLoading={isLoading} />
    </>
  );
}

export default App;
